INSERT INTO `binhluan` (`idBL`, `tacgia`, `noidung`, `ngaydang`, `parent`, `tinslug`) VALUES
(1, 'Ngoc Bich', 'Gan day t co trieu chug met moi.an ko ngon. Lười di lai. Sag ngu day cơ the met moi. Mat thi noi nhiu mun. Bug thi dau âm ỉ hoai. Nhờ bsi tu van giup ạ ko pit do co phai trieu chug cia beh gan suy yeu ko ạ', '2017-04-14 09:04:46', 0, 'cac-trieu-chung-cua-benh-viem-gan-1977'),
(2, 'Ngoc Bich 2', 'Gan day t co trieu chug met moi.an ko ngon. Lười di lai. Sag ngu day cơ the met moi. Mat thi noi nhiu mun. Bug thi dau âm ỉ hoai. Nhờ bsi tu van giup ạ ko pit do co phai trieu chug cia beh gan suy yeu ko ạ', '2017-04-14 09:04:46', 0, 'cac-trieu-chung-cua-benh-viem-gan-1977'),
(3, 'Ngoc Bich 3', 'Gan day t co trieu chug met moi.an ko ngon. Lười di lai. Sag ngu day cơ the met moi. Mat thi noi nhiu mun. Bug thi dau âm ỉ hoai. Nhờ bsi tu van giup ạ ko pit do co phai trieu chug cia beh gan suy yeu ko ạ', '2017-04-14 09:04:46', 2, 'cac-trieu-chung-cua-benh-viem-gan-1977'),
(7, 'ĐInh Văn Sang', 'Nội dung mới', '2017-04-16 01:38:03', 0, 'cac-trieu-chung-cua-benh-viem-gan-1977');
INSERT INTO `cauhoi` (`idCH`, `idCL`, `TieuDeCH`, `TieuDeCHKD`, `KeywordCH`, `DescriptionCH`, `HoTen`, `Email`, `DienThoai`, `NoiDungCH`, `AnHien`, `NgayTao`, `TongTL`) VALUES
(117, 1, 'nhờ bs giải đáp gấp', 'nho-bs-giai-dap-gap', '', '2 ng&agrave;y nay tinh ho&agrave;n b&ecirc;n tr&aacute;i của t&ocirc;i tự nhi&ecirc;n cảm gi&aacute;c đau, m&agrave; tiểu tiện cũng kh&oacute; chịu ...', 'tuấn Hải', '', '0907236468', '<p>2 ng&agrave;y nay tinh ho&agrave;n b&ecirc;n tr&aacute;i của t&ocirc;i tự nhi&ecirc;n cảm gi&aacute;c đau, m&agrave; tiểu tiện cũng kh&oacute; chịu nữa, quan hệ với vợ cũng cảm thấy kh&oacute; khăn... b&acirc;y giờ t&ocirc;i cảm thấy ngại với vụ n&agrave;y, giờ t&ocirc;i phải l&agrave;m sao đ&acirc;y bs? t&ocirc;i chưa d&aacute;m n&oacute;i cho vợ con t&ocirc;i biết g&igrave; hết &aacute;</p>\r\n', 1, '2014-11-14', 3);
INSERT INTO `dangky` (`idDK`, `HoTen`, `Tuoi`, `GioiTinh`, `DThoai`, `Email`, `DiaChi`, `NgayDK`, `Chuyenkhoa`, `GhiChu`, `TrieuChung`) VALUES
(64, 'jialing', 24, 0, '0912345563', 'Email', 'DiaChi', '2014-09-28', 'phu-khoa', 'ghichu', ''),
(65, 'BJKIBHJUIHU', 0, 1, '0909998877', 'Email', 'DiaChi', '2014-10-03', 'nam-khoa', 'ghichu', 'OJOKOJ'),
(66, 'Nguyễn Thanh Tuyền', 22, 0, '0993107617', 'Email', 'DiaChi', '2014-10-10', 'phu-khoa', 'ghichu', 'em bị  đau bụng dưới đột ngột, đau tăng khi đi lại, buồn nôn...'),
(67, 'Hồ Thị Diễm', 22, 0, '01694301530', 'Email', 'DiaChi', '2014-10-20', 'phu-khoa', 'ghichu', 'kinh nguyệt không đều,đau bụng trong chu kỳ kinh,mệt mỏi\r\nngứa âm đạo,khó chịu\r\ntiểu khó,tiểu buốt'),
(68, 'Nguyễn Thị Khìn', 25, 0, '0987187144', 'Email', 'DiaChi', '2014-10-10', 'phu-khoa', 'ghichu', 'em bị  đau bụng dưới đột ngột, đau tăng khi đi lại, buồn nôn...'),
(69, 'Trịnh Mỹ Linh', 28, 0, '0916077078', 'Email', 'DiaChi', '2014-10-10', 'phu-khoa', 'ghichu', 'âm hộ,âm đạo ngứa,cảm giác bỏng rát.khí hư đặc có màu trắng sữa, có mùi hôi,cảm thấy tiểu khó tiểu buốt'),
(70, 'Hồ Diễm hiền', 22, 0, '01694301530', 'Email', 'DiaChi', '2014-10-12', 'vo-sinh', 'ghichu', 'em và chồng em đã cưới nhau được hơn 2 năm mà chưa có em bé,em thật lo lắng'),
(71, 'Nguyễn An', 23, 0, '01695031025', 'Email', 'DiaChi', '2014-10-10', 'phu-khoa', 'ghichu', 'Cho em hỏi 3 tháng nay chu kỳ kinh nguyệt của em không đều, .ỗi chu kỳ kinh nguyệt kéo dài hơn 35 ngày, mỗi lần hành kinh áu ra nhiều, vón cục có màu nâu sẫm.kèm theo đau bụng kinh kinh.Tháng nay em lại bị chậm kinh 10 ngày rồi. cho em hỏi  những dấu hiệu này có gây ảnh hưởng tới sức khỏe sinh sản không?'),
(72, 'Trần Thị Bé', 40, 0, '0982290215', 'Email', 'DiaChi', '2014-10-22', 'phu-khoa', 'ghichu', 'Tôi bị bệnh trĩ, và  đi khám trĩ, sau khi khám Bác sỹ bảo tôi bị viêm âm đạo,nói sau trị trĩ nên trị bệnh viêm âm đạo,tôi lo lắng không biết tại sao lại bị và nên trị như thế nào?.và tôi thấy triệu chứng khí hư màu vàng,không ngứa,có khi ra rất nhiều.'),
(73, 'Kiem tra', 35, 1, '0933969108', 'Email', 'DiaChi', '2014-12-05', 'nam-khoa', 'ghichu', 'ghjkhjkl'),
(74, 'Chao Chen', 25, 1, '0909090909', 'Email', 'DiaChi', '2014-12-16', 'nam-khoa', 'ghichu', 'asdasd'),
